let slideIndex = [1, 1, 1];
let slideId = [".slides-services", ".slides-projects", ".slides-us"];

function plusSlides(n, no) {
  showSlides((slideIndex[no] += n), no, n);
}

function showSlides(n, no, originalValue) {
  let i;
  let x = document.querySelectorAll(slideId[no]);
  if (n > x.length) {
    slideIndex[no] = 1;
  }
  if (n < 1) {
    slideIndex[no] = x.length;
  }
  for (i = 0; i < x.length; i++) {
    if (originalValue === -1) {
      byeLeft(x[i]);
      entranceFromLeft(x[slideIndex[no] - 1]);
    } else {
      bye(x[i]);
      entranceFromRight(x[slideIndex[no] - 1]);
    }
  }
  x[slideIndex[no] - 1].style.display = "grid";
}

function listenToScreenWidth(w) {
  if (w <= targetWidth) {
    sections.forEach(section => {
      if (section.classList.contains("active")) {
        if (section.id === "service") {
          printServices(section);
        } else if (section.id === "project") {
          showSlides(1, 1);
        } else if (section.id === "us") {
          showSlides(1, 2);
        }
      }
    });
  } else {
    sections.forEach(section => {
      if (section.classList.contains("active")) {
        if (section.id === "service") {
          servicesSection.innerHTML = "";
          servicesSection.insertAdjacentHTML("beforeend", carouselServices);
          fadeIn(section, document.getElementById("services-title"));
        }
      }
    });
  }
}

async function printServices(section) {
  let promise = new Promise(function(resolve) {
    servicesSection.innerHTML = "";
    resolve(servicesSection.insertAdjacentHTML("beforeend", carouselServices));
  });
  await promise;
  fadeIn(section, document.getElementById("services-title"));
  showSlides(1, 0);
  document.getElementById("next-service").addEventListener("click", e => {
    e.preventDefault();
    plusSlides(1, 0);
  });
}

/* ------------------------------------- GSAP Animation functions ------------------------------------- */

function fadeIn(section, sectionTitle) {
  const tl = new TimelineMax();
  tl.fromTo(
    section,
    1.5,
    {
      xPercent: -100
    },
    {
      xPercent: 0,
      zIndex: 1
    },
    0.1
  )
    .fromTo(
      ".arrows",
      1,
      {
        opacity: 0
      },
      {
        opacity: 1
      },
      1.3
    )
    .fromTo(
      sectionTitle,
      1,
      {
        opacity: 0
      },
      {
        opacity: 1,
        ease: "power2.out"
      },
      1.3
    );
}

function entranceFromRight(elementOne) {
  TweenMax.fromTo(
    elementOne,
    1,
    {
      xPercent: -200,
      width: 0,
      opacity: 0
    },
    {
      xPercent: 0,
      width: "80%",
      opacity: 1
    }
  );
}

function entranceFromLeft(elementOne) {
  TweenMax.fromTo(
    elementOne,
    1,
    {
      xPercent: 200,
      width: 0,
      opacity: 0
    },
    {
      xPercent: 0,
      width: "80%",
      opacity: 1
    }
  );
}

function bye(element) {
  TweenMax.to(element, 1, {
    xPercent: 200,
    width: 0,
    opacity: 0
  });
}

function byeLeft(element) {
  TweenMax.to(element, 1, {
    xPercent: -200,
    width: 0,
    opacity: 0
  });
}

const zeppelinFloating = TweenMax.to(zeppelin, 2, {
  y: "-=15px",
  yoyo: true,
  repeat: -1,
  ease: Power0.easeNone,
  paused: false
});

function playZeppelin() {
  zeppelinFloating.play();
}

function homeBackgroundExit() {
  const tl = new TimelineMax();
  tl.to(
    mountainOne,
    1.5,
    {
      onStart: focusAnchor(mountainOne)
    },
    0.1
  )
    .to(
      mountainTwo,
      1,
      {
        onStart: focusAnchor(mountainTwo)
      },
      0.1
    )
    .to(
      cities,
      1,
      {
        opacity: 0
      },
      "-0.1"
    )
    .to(
      tagline,
      0.3,
      {
        opacity: 0
      },
      0.1
    )
    .to(
      logoWLetters,
      0.5,
      {
        fill: "#ffffff",
        ease: "sine.out"
      },
      0.1
    )
    .to(
      logoMobile,
      1,
      {
        onStart: focusAnchor(logoMobile)
      },
      0.1
    )
    .to(
      logoSvg,
      1,
      {
        onStart: focusAnchor(logoSvg)
      },
      0.1
    )
    .to(
      ".web-studio",
      0.5,
      {
        fill: "transparent"
      },
      0.1
    );
}

function homeBackgroundEntrance() {
  const tl = new TimelineMax();
  tl.to(
    mountainOne,
    1.5,
    {
      onStart: removeClass(mountainOne),
      onComplete: playZeppelin()
    },
    0.1
  )
    .to(
      mountainTwo,
      1,
      {
        onStart: removeClass(mountainTwo)
      },
      0.1
    )
    .to(
      cities,
      1,
      {
        opacity: 1
      },
      0.7
    )
    .fromTo(
      home,
      1.5,
      {
        xPercent: -100
      },
      {
        xPercent: 0,
        zIndex: 1
      },
      0.1
    )
    .to(
      tagline,
      1,
      {
        opacity: 1
      },
      0.7
    )
    .to(
      logoWLetters,
      0.1,
      {
        clearProps: "all"
      },
      "-0.5"
    )
    .fromTo(
      logoMobile,
      1.5,
      {
        opacity: 0
      },
      {
        opacity: 1
      },
      0.5
    )
    .to(
      logoMobile,
      1.5,
      {
        onStart: removeClass(logoMobile)
      },
      0.1
    )
    .to(
      logoSvg,
      1,
      {
        onStart: removeClass(logoSvg)
      },
      0.1
    )
    .to(
      ".web-studio",
      1.5,
      {
        fill: "#000000"
      },
      0.5
    );
}

